import { NextRequest, NextResponse } from 'next/server'
import { uploadToCloudinary } from '@/lib/cloudinary'

export async function POST(request: NextRequest) {
  try {
    // Test Cloudinary connection with a simple text-based image
    const testImageBase64 = 'data:image/svg+xml;base64,'
    
    const result = await uploadToCloudinary(testImageBase64, {
      folder: 'repair-connect/test',
      publicId: `test-${Date.now()}`,
      resourceType: 'image',
      tags: ['test']
    })

    return NextResponse.json({
      success: true,
      message: 'Cloudinary connection test successful',
      data: result
    })
  } catch (error) {
    console.error('Cloudinary test error:', error)
    return NextResponse.json({
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      details: error
    }, { status: 500 })
  }
}
